    let postData = await parsePostData(ctx)
    ctx.body = postData
} else {
    // 其他请求显示404
    ctx.body = '404！！！ o(╯□╰)o'
    }
})

// 解析上下文里node原生请求的POST参数
function parsePostData(ctx) {
    return new Promise((resolve, reject) => {
        try {
